clanstatswide = ''

clanstatswide_byte_field = {
    0:"kills",
    4:"deaths",
    8:"wins",
    12:"losses",
}

def HexToClanstatswide(hx):
    '''
    Accepts clanstatswide packet
    
    returns dict of the basic clan stats'''
    output = {
        'kills':0,
        'deaths':0,
        'wins':0,
        'losses':0
    }
    byte_index = 0
    for i in range(0, len(hx), 8):
        hex_field = hx[i:i+8]
        little_endian = []
        for j in range(0,8,2):
            bits = hex_field[j:j+2]
            little_endian.append(bits)
        little_endian = "".join(little_endian[::-1])
        value = int(little_endian, 16) #big endian int 32
        if byte_index in clanstatswide_byte_field:
            field = clanstatswide_byte_field[byte_index]
        else:
            field = None
        
        if field != None:
            output[field] = value
        byte_index+=4
    return output
    

print(HexToClanstatswide(clanstatswide))