def getClanId(stats):
    '''stats is player 'stats' packet
    returns player clan id'''

    i = 9*8 #9th position * 8 characters per position
    hex_field = stats[i:i+8]

    if hex_field == 'FFFFFFFF' or hex_field == '00000000': #no clan
        return {'clan_id':-1}

    little_endian = []
    for j in range(0,8,2):
        bits = hex_field[j:j+2]
        little_endian.append(bits)
    little_endian = "".join(little_endian[::-1])
    value = int(little_endian, 16) #big endian int 32
    return {'clan_id': value}



